/*  NAV ACTIVE SECTION DETECTION  */
(function(){
  // Map: nav data-section  which page section IDs count as "that nav item"
  const sectionMap = {
    platform:  ['platform','nudges','ai-nlq','pain'],
    solutions: ['solutions','roi'],
    industries:['industries','terminal-sec','tour','partners'],
    resources: ['compliance','resources'],
    pricing:   ['pricing'],
    about:     ['why','trust','success','about','cta']
  };

  const navLinks = document.querySelectorAll('.nav-links a[data-section]');

  function setActive(sectionKey){
    navLinks.forEach(a=>{
      a.classList.toggle('active', a.dataset.section === sectionKey);
    });
  }

  // Build list of [sectionId, navKey] pairs in page order
  const pairs = [];
  Object.entries(sectionMap).forEach(([navKey, ids])=>{
    ids.forEach(id=>{ if(document.getElementById(id)) pairs.push([id, navKey]); });
  });
  // Sort by DOM position
  pairs.sort((a,b)=>{
    const eA = document.getElementById(a[0]);
    const eB = document.getElementById(b[0]);
    return eA.getBoundingClientRect().top - eB.getBoundingClientRect().top;
  });

  function updateActive(){
    const mid = window.innerHeight * 0.4;
    let active = pairs[0][1];
    for(const [id, navKey] of pairs){
      const el = document.getElementById(id);
      if(!el) continue;
      const top = el.getBoundingClientRect().top;
      if(top <= mid) active = navKey;
    }
    setActive(active);
  }

  window.addEventListener('scroll', updateActive, {passive:true});
  updateActive();
})();

