/*  CHART (Gap #15: static SVG placeholder, canvas replaces)  */
function drawChart(){
  const canvas=document.getElementById('chartCanvas');if(!canvas)return;
  const W=canvas.offsetWidth||700,H=220;
  canvas.width=W;canvas.height=H;
  const ctx=canvas.getContext('2d');
  const pad={t:20,r:24,b:36,l:50},n=48;
  const iW=W-pad.l-pad.r,iH=H-pad.t-pad.b;
  const data=Array.from({length:n},(_,i)=>195+Math.sin(i*.18)*38+(Math.random()-.5)*18+i*.4);
  const xp=i=>pad.l+(i/(n-1))*iW,yp=v=>pad.t+iH-((v-150)/180)*iH;
  /* Grid */
  ctx.strokeStyle='rgba(255,255,255,.04)';ctx.lineWidth=1;
  for(let i=0;i<=4;i++){const y=pad.t+(i/4)*iH;ctx.beginPath();ctx.moveTo(pad.l,y);ctx.lineTo(W-pad.r,y);ctx.stroke();ctx.fillStyle='rgba(255,255,255,.18)';ctx.font='9px JetBrains Mono';ctx.fillText(Math.round(325-i*40),4,y+4);}
  /* Threshold */
  ctx.beginPath();ctx.setLineDash([5,7]);ctx.strokeStyle='rgba(34,197,94,.55)';ctx.lineWidth=1.5;ctx.moveTo(pad.l,yp(280));ctx.lineTo(W-pad.r,yp(280));ctx.stroke();ctx.setLineDash([]);
  /* Baseline */
  ctx.beginPath();ctx.setLineDash([2,5]);ctx.strokeStyle='rgba(255,255,255,.12)';ctx.lineWidth=1;ctx.moveTo(pad.l,yp(195));ctx.lineTo(W-pad.r,yp(195));ctx.stroke();ctx.setLineDash([]);
  /* Area */
  const g=ctx.createLinearGradient(0,pad.t,0,H);g.addColorStop(0,'rgba(0,170,255,.2)');g.addColorStop(1,'rgba(0,170,255,0)');
  ctx.beginPath();ctx.moveTo(xp(0),yp(data[0]));data.forEach((v,i)=>{if(i>0)ctx.lineTo(xp(i),yp(v));});ctx.lineTo(xp(n-1),pad.t+iH);ctx.lineTo(xp(0),pad.t+iH);ctx.closePath();ctx.fillStyle=g;ctx.fill();
  /* Line */
  ctx.beginPath();ctx.strokeStyle='#00AAFF';ctx.lineWidth=2;ctx.lineJoin='round';ctx.moveTo(xp(0),yp(data[0]));data.forEach((v,i)=>{if(i>0)ctx.lineTo(xp(i),yp(v));});ctx.stroke();
  /* Dot */
  const lx=xp(n-1),ly=yp(data[n-1]);ctx.beginPath();ctx.arc(lx,ly,4,0,Math.PI*2);ctx.fillStyle='#00AAFF';ctx.fill();ctx.beginPath();ctx.arc(lx,ly,8,0,Math.PI*2);ctx.strokeStyle='rgba(0,170,255,.3)';ctx.lineWidth=1.5;ctx.stroke();
  /* X labels */
  for(let i=0;i<7;i++){const xi=Math.round(i*(n-1)/6);ctx.fillStyle='rgba(255,255,255,.15)';ctx.font='9px JetBrains Mono';ctx.fillText(`${i*4}h`,xp(xi)-6,H-4);}
  /* Show canvas, hide SVG */
  document.getElementById('chartSVG').classList.add('ready');
  document.getElementById('chartCanvas').classList.add('ready');
}
setTimeout(drawChart,200);
