/*  ROI CALCULATOR  */
const CURRENCIES = {
  EUR: { sym: '', rate: 1,     label: 'EUR' },
  USD: { sym: '$', rate: 1.09,  label: 'USD' },
  GBP: { sym: '', rate: 0.84, label: 'GBP' },
  INR: { sym: '', rate: 91,   label: 'INR' }
};
function getCurrency(){ return CURRENCIES[document.getElementById('roi-currency')?.value||'EUR']||CURRENCIES.EUR; }
function fmtCurrency(n, cur){
  const v = n * cur.rate;
  if(Math.abs(v) >= 1e7) return cur.sym+(v/1e6).toFixed(1)+'M';
  if(Math.abs(v) >= 1e6) return cur.sym+(v/1e6).toFixed(1)+'M';
  if(Math.abs(v) >= 1e3) return cur.sym+Math.round(v/1e3)+'K';
  return cur.sym+Math.round(v);
}
function updateT(){document.getElementById('roi-tv').textContent=(+document.getElementById('roi-tonnes').value).toLocaleString()+' tCO\u2082e';}
function updateC(){
  const cur=getCurrency();
  const val=+document.getElementById('roi-cost').value;
  document.getElementById('roi-cv').textContent=cur.sym+(val*cur.rate/1000).toFixed(0)+'K/year';
}
function calcROI(){
  const cur=getCurrency();
  const sites=Math.max(1,+document.getElementById('roi-sites').value||5);
  const tonnes=+document.getElementById('roi-tonnes').value||500000;
  const cost=+document.getElementById('roi-cost').value||800000;
  const cbam=+document.getElementById('roi-cbam').value;
  // Penalty: EU ETS base 62/tCO2e, conservative benchmark
  const penEUR=Math.round(tonnes*.05*62*(cbam?1.35:1));
  const rep=Math.round(cost*.72);
  document.getElementById('out-pen').textContent=fmtCurrency(penEUR, cur);
  document.getElementById('out-rep').textContent=fmtCurrency(rep, cur);
  document.getElementById('out-tot').textContent=fmtCurrency(penEUR+rep, cur);
  // Update currency label on outputs
  document.querySelectorAll('.roi-currency-note').forEach(el=>{ el.textContent = cur.label; });
}
document.getElementById('roi-currency')?.addEventListener('change', ()=>{ updateC(); calcROI(); });
calcROI();

